(1, '<div class=\'q-learn\'>JS</div><div class=\'q-title\'>Membuat Variabel</div><div class=\'q-regular\'>Buat variabel yang dinamakan carName, tetapkan nilai \'Avanza\' ke dalamnya<br></div>', 'var <input class=\'anwer-input-user answer0\' data-answer=\'carName\' maxlength=\'7\' size=\'7\'> = \'<input class=\'anwer-input-user answer1\' data-answer=\'Avanza\' maxlength=\'6\' size=\'6\'>\';<div><br></div><div><input type=\'hidden\' class=\'count-input\' value=\'2\'></div><div class=\'answer-alert\'></div>\r\n<style type=\'text/css\'>\r\n	.answer-alert {\r\n		position: fixed;\r\n		width: 100%;\r\n		height: 100%;\r\n		top: 0;\r\n		left: 0;\r\n		z-index: 99;\r\n		background: rgba(0,0,0,0.7);\r\n		display: grid;\r\n		align-items: center;\r\n		justify-content: center;\r\n		font-family: arial;\r\n		font-size: 50px;\r\n		color: #FFF;\r\n		user-select: none;\r\n	}\r\n</style>\r\n<script type=\'text/javascript\'>\r\n	$(\'.ab-show\').on(\'click\', function(){\r\n		check = $(\'.anwer-input-user\').val();\r\n		if (check == \'\') {\r\n			$(\'.anwer-input-user\').each(function(){\r\n				answer = $(this).attr(\'data-answer\');\r\n				$(this).val(answer);\r\n			});\r\n		} else {\r\n			$(\'.anwer-input-user\').each(function(){\r\n				$(this).val(\'\');\r\n			});\r\n		}\r\n	});\r\n\r\n	$(\'.answer-alert\').hide();\r\n	$(\'.ab-run\').on(\'click\', function(){\r\n		$(\'.anwer-input-user\').each(function(){\r\n			realAnswer = $(this).attr(\'data-answer\');\r\n			userAnswer = $(this).val();\r\n			if (realAnswer == userAnswer) {\r\n				$(\'.answer-alert\').html(\'Jawaban Benar!\').css({\'color\':\'#4caf04\'}).fadeIn();\r\n			} else {\r\n				$(\'.answer-alert\').html(\'Jawaban Salah!\').css({\'color\':\'#f44336\'}).fadeIn();\r\n			}\r\n		})\r\n	});\r\n	$(\'.answer-alert\').on(\'click\', function(){\r\n		$(\'.answer-alert\').fadeOut();\r\n	});\r\n</script>', '<script>var carName= \'Avanza\';</script>', 'yandy6man'),
