(19, '<div class=\'q-learn\'>SQL</div><div class=\'q-title\'>Fungsi SQL</div><div class=\'q-regular\'>Gunakan fungsi SQL untuk menghitung jumlah semua nilai kolom &nbsp;<span class=\'q-code\'> Price </span>&nbsp; dalam tabel &nbsp;<span class=\'q-code\'> Product </span>&nbsp;<br></div>', '<div>SELECT <input class=\'anwer-input-user answer0\' data-answer=\'SUM(Price)\' maxlength=\'10\' size=\'10\'></div><div>FROM Products;</div><div><br></div><div><input type=\'hidden\' class=\'count-input\' value=\'1\'></div><script type=\'text/javascript\'>\r\n	$(\'.ab-show\').on(\'click\', function(){\r\n		check = $(\'.anwer-input-user\').val();\r\n		if (check == \'\') {\r\n			$(\'.anwer-input-user\').each(function(){\r\n				answer = $(this).attr(\'data-answer\');\r\n				$(this).val(answer);\r\n			});\r\n		} else {\r\n			$(\'.anwer-input-user\').each(function(){\r\n				$(this).val(\'\');\r\n			});\r\n		}\r\n	});\r\n</script>', '<div style=\"font-family: monospace;\"> +--------+<br> |&nbsp;Price&nbsp;&nbsp;|<br> +--------+<br> |&nbsp;157800&nbsp;|<br> +--------+ </div>', 'yandy6man'),
(20, '<div class=\'q-learn\'>SQL</div><div class=\'q-title\'>Menampilkan LIKE</div><div class=\'q-regular\'>Pilih semua &nbsp;<b> record </b>&nbsp; di mana nilai kolom &nbsp;<span class=\'q-code\'> City </span>&nbsp; dimulai dengan huruf \'a\'<br></div>', '<div>SELECT * FROM Customer</div><div><input class=\'anwer-input-user answer0\' data-answer=\'WHERE City LIKE \'a%\'\' maxlength=\'20\' size=\'20\'>;</div><div><br></div><div><input type=\'hidden\' class=\'count-input\' value=\'1\'></div><div class=\'answer-alert\'></div>\r\n<style type=\'text/css\'>\r\n	.answer-alert {\r\n		position: fixed;\r\n		width: 100%;\r\n		height: 100%;\r\n		top: 0;\r\n		left: 0;\r\n		z-index: 99;\r\n		background: rgba(0,0,0,0.7);\r\n		display: grid;\r\n		align-items: center;\r\n		justify-content: center;\r\n		font-family: arial;\r\n		font-size: 50px;\r\n		color: #FFF;\r\n		user-select: none;\r\n	}\r\n</style>\r\n<script type=\'text/javascript\'>\r\n	$(\'.ab-show\').on(\'click\', function(){\r\n		check = $(\'.anwer-input-user\').val();\r\n		if (check == \'\') {\r\n			$(\'.anwer-input-user\').each(function(){\r\n				answer = $(this).attr(\'data-answer\');\r\n				$(this).val(answer);\r\n			});\r\n		} else {\r\n			$(\'.anwer-input-user\').each(function(){\r\n				$(this).val(\'\');\r\n			});\r\n		}\r\n	});\r\n\r\n	$(\'.answer-alert\').hide();\r\n	$(\'.ab-run\').on(\'click\', function(){\r\n		$(\'.anwer-input-user\').each(function(){\r\n			realAnswer = $(this).attr(\'data-answer\');\r\n			userAnswer = $(this).val();\r\n			if (realAnswer == userAnswer) {\r\n				$(\'.answer-alert\').html(\'Jawaban Benar!\').css({\'color\':\'#4caf04\'}).fadeIn();\r\n			} else {\r\n				$(\'.answer-alert\').html(\'Jawaban Salah!\').css({\'color\':\'#f44336\'}).fadeIn();\r\n			}\r\n		})\r\n	});\r\n	$(\'.answer-alert\').on(\'click\', function(){\r\n		$(\'.answer-alert\').fadeOut();\r\n	});\r\n</script>', '<div style=\"font-family: monospace;\">+-----------+<br>|&nbsp;City&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;|<br>+-----